<template>
  <div class="text-center">
    <v-menu
        offset-y
    >
      <template v-slot:activator="{ on, attrs }">
        <div class="d-flex" v-if="auth">
          <svg
              width="50"
              viewBox="0 0 48 48"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
              xmlns:xlink="http://www.w3.org/1999/xlink"
              v-bind="attrs"
              v-on="on"
          >
            <circle cx="24" cy="24" r="24" fill="#D9D9D9"/>
            <circle cx="24" cy="24" r="24" fill="url(#pattern0)"/>
            <defs>
              <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
                <use xlink:href="#image0_417_1366" transform="translate(0 -0.183761) scale(0.002849)"/>
              </pattern>
              <image id="image0_417_1366" width="351" height="480" xlink:href="data:image/jpeg;base64,"/>
            </defs>
          </svg>
        </div>
          <v-list-item-avatar v-else>
            <v-img
                v-bind="attrs"
                v-on="on"
                max-width="60"
                max-height="60"
                src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQzPb_pSj-ir-9eB6mi0lVJdQP1KKHiB8fRBS1CbmOXGd9Z1FEGMJHbEKhahwhWLGSaEXY&usqp=CAU"
                alt="John"
            >
            </v-img>
          </v-list-item-avatar>

      </template>

      <v-card class="rounded-lg">
        <v-list>
          <v-list-item v-for="(item, idx) in menu" :key="idx">
            <v-list-item-title v-if="item.function" @click="router()" style="cursor:pointer;">{{item.label}}</v-list-item-title>
            <v-list-item-title v-else-if="item.click" @click="$store.state.login[item.click] = true" style="cursor:pointer;">{{item.label}}</v-list-item-title>
            <router-link v-else :to="item.link" class="text-decoration-none black--text">
              <v-list-item-title>{{item.label}}</v-list-item-title>
            </router-link>
          </v-list-item>
        </v-list>
      </v-card>
    </v-menu>
  </div>
</template>

<script>
import {getToken, removeToken} from "@/helpers/token";
export default {
  name: "userMenu",
  data: () => ({
    authMenu: [
      {label: 'Сообщения', link: '/'},
      {label: 'Профиль', link: '/profile'},
      {label: 'План маршрута', link: '/'},
      {label: 'Открыть гостевой дом', link: '/page-create'},
      {label: 'Предложить услугу', link: '/'},
      {label: 'Выйти', link: '/', function: true},
    ],
    unAuthMenu: [
      {label: 'Зарегистрироваться', link: '/', click: 'dialog'},
      {label: 'Войти', link: '/', click: 'dialog'},
    ]
  }),
  computed: {
    menu()  {
      return getToken() ? this.authMenu : this.unAuthMenu
    },
    auth()  {
      return !!getToken()
    },
  },
  methods: {
    router() {
      removeToken()
      location.reload()
    }
  }
}
</script>

